mapper.select:
		select
		
		, o.REMARK
		FROM t_employee z1 LEFT JOIN(
		select * from t_offline_oper
		where 1=1
		

			AND YEaR = #{year}
		
		
			AND MONTH = #{month}
		
		
			AND RM_ID = #{rmId}
		
		) o
		ON z1.EMPLOYEE_ID = o.EMPLOYEE_ID
		WHERE 1=1
		AND z1.RM_USER_ID = #{rmId}
		AND z1.RESOURCE_STATUS='Active'
		
			AND z1.STAFF_NAME like concat(concat('%',#{staffName}),'%')
		
		
			AND z1.E_HR = #{eHr}
		
		
			AND z1.CHSOFTI_PRO_NUMBER = #{proNumber}
		
		
			AND z1.CHSOFTI_PRO_NAME = #{projectName}
		
		
			AND z1.CS_SUBDEPT_ID = #{csdeptid}
		
		ORDER BY o.e_hr DESC

select

, o.REMARK
FROM t_employee z1 LEFT JOIN(
select * from t_offline_oper
where 1=1

	AND YEAR = YEAR(NOW())


	AND MONTH = MONTH(NOW())


	AND CS_SUBDEPT_ID = #{csdeptid}

) o
ON z1.EMPLOYEE_ID = o.EMPLOYEE_ID
WHERE 1=1
AND z1.RESOURCE_STATUS='Active'

	AND z1.STAFF_NAME like concat(concat('%',#{staffName}),'%')


	AND z1.E_HR = #{eHr}


	AND z1.CHSOFTI_PRO_NUMBER = #{proNumber}


	AND z1.CHSOFTI_PRO_NAME = #{projectName}


	AND z1.CS_SUBDEPT_ID = #{csdeptid}

ORDER BY o.e_hr DESC

